const MainLayout = ({ children }) => {
  return (
    <div className="main-container">
      <div className="content-wrapper">{children}</div>

      <style jsx global>{`
        *,
        *::before,
        *::after {
          box-sizing: border-box;
        }

        main-container: {
          width: 100vw;
          margin: auto;
        }
      `}</style>
    </div>
  );
};

export default MainLayout;
